const char* const network_test_script =
"function xinspect(o,i){\nif(typeof i=='undefined')i='';\nif(i.length>50) return '[MAX ITERATIONS]';\nvar r=[];\nfor(var p in o){\nvar t=typeof o[p];\nr.push(i+'\\''+p+'\\' ('+t+') => '+(t=='object' ? 'object:'+xinspect(o[p],i+'  ') : o[p]+''));\n}\nreturn r.join(i+'\\n');\n}\n\nvar http = require('http');\n\nvar WIFI = require('wifi').WiFi;\nvar wifi = new WIFI();\nprint('wifi == ' + xinspect(wifi) + '\\n');\n\nwifi.assoc_cb = function() {\nprint('assoc_cb\\n');\nhttp.get({\nhost: '192.168.17.103',\nport: 3000,\npath: '/'\n}, function(resp) {\nvar str = '';\nresp.on('data', function(chunk) {\nconsole.log('chunk: ' + chunk);\nstr += chunk;\n});\nresp.on('data', function(chunk) {\nconsole.log('resp.end : ' + str);\n});\n});\n};\n\nwifi.connect_cb = function() {\nprint('connect_cb\\n');\n};\n\nwifi.disconnect_cb = function() {\nprint('disconnect_cb\\n');\n};\n\nwifi.connect({\nssid:'',\npassword:''\n}, function(err) {\nprint('connect ret:' + err);\n});\n";
